#include<stdio.h>
#include<string.h>

int main() {
  int sum = count_sum();
  int sum2 = count_sum2();
  printf("Day 1 pt 1! Sum: %d Sum2: %d \n", sum);
}

int count_sum() {
  int sum = 0;
  char *str = "";
  char *fst = str;
  char *snd = str;
  snd ++;
  while(*snd) {
    if(*fst == *snd) {
      int fst_val = *fst - '0';
      sum += fst_val;
    }
    fst ++;
    snd ++;
  }
  return sum;
}

int count_sum2() {
  int sum = 0;
  int startpos = 0;
  char *str = "";
  char *fst = str;
  char *snd = str;
  int len = strlen(str);
  startpos = len / 2;
  while(*snd) {
    snd = startpos % len;
    if(*fst == *snd) {
      int fst_val = *fst - '0';
      sum += fst_val;
    }
    fst ++;
    startpos ++;
  }
  return sum;
}
