#include<stdio.h>
#include<string.h>

int main() {
  int sum = count_sum();
  printf("Day 1 pt 1! Sum: %d\n", sum);
}

int count_sum() {
  int sum = 0;
  char *str = "";
  char *fst = str;
  char *snd = str;
  snd ++;
  while(*snd) {
    if(*fst == *snd) {
      int fst_val = *fst - '0';
      sum += fst_val;
    }
    fst ++;
    snd ++;
  }
  return sum;
}
